let contactProp;
let customer = steps.loopOverSubscriptions.entry.customer;
let subscription = steps.loopOverSubscriptions.entry.subscription;

contactProp = {
   "email_address": customer.email,
      "email_type": "html",
       "merge_fields": {
         	"FNAME": customer.last_name || '',
        	"LNAME": customer.last_name || '',
          "REVENUE": subscription.mrr || '',
          "SUB_HANDLE": subscription.id || '',
          "PLAN_CODE": 'cbdemo_hustle',
          "PLAN_NAME": subscription.plan_name || '',
          "SUB_SINCE": new Date(subscription.created_at) || '',
          "SUB_STATUS": 'Active',
         	"IS_TRIAL": '',
          "TRIAL_END": subscription.trial_end || '',
          "CANCEL_RSN": subscription.cancel_reason||'',
      },
      "status": "subscribed"
};

done({
  props: contactProp
})
